from ast import literal_eval

features = ['cast', 'crew']
for feature in features:
  credits[feature] = credits[feature].apply(literal_eval)

"""### Director"""

def get_director(x):
  for i in x:
    if i['job'] == 'Director':
      return i['name']

  return np.nan

credits['director'] = credits['crew'].apply(get_director)

"""### Writer"""

def get_writers(x):
  writers = []
  for i in x:
    if i['job'] == 'Screenplay':
      writers.append(i['name'])

  return writers

credits['writers'] = credits['crew'].apply(get_writers)

"""### Composer"""

def get_music(x):
  for i in x:
    if i['job'] == 'Original Music Composer':
      return i['name']

  return np.nan

credits['composer'] = credits['crew'].apply(get_music)

"""### Lead actor gender"""

def get_actors(x):
  actors = []
  for i in x[:3]:
    actors.append(i['name'])

  return actors

credits['actors'] = credits['cast'].apply(get_actors)

def male_lead_actor(x):
  for i in x[:1]:
    return 1 if i['gender'] == 2 else 0
  return 0

"""### Cast gender ratio"""

credits['lead_actor_male'] = credits['cast'].apply(male_lead_actor)

def cast_gender_ratio(x):
  total = 0
  male = 0
  for i in x[:5]:
    if i['gender'] == 2:
      male+=1
      total+=1
    else:
      total+=1
  if total == 0:
    return 0
  return male / total

credits['male_cast_ratio'] = credits['cast'].apply(cast_gender_ratio)

"""### Crew gender ratio"""

def crew_gender_ratio(x):
  total = 0
  male = 0
  for i in x[:5]:
    if i['gender'] == 2:
      male+=1
      total+=1
    else:
      total+=1
  if total == 0:
    return 0
  return male / total

credits['male_crew_ratio'] = credits['crew'].apply(cast_gender_ratio)

