spring = range(80, 172)
summer = range(172, 264)
fall = range(264, 355)

def get_season(date):
  doy = date.timetuple().tm_yday
  if doy in spring:
    return 'Spring'
  elif doy in summer:
    return 'Summer'
  elif doy in fall:
    return 'Fall'
  else:
    return 'Winter'

df['season'] = df['release_date'].apply(get_season)

